import React from 'react';
import {connect} from "react-redux";
import {loadNextPageFromDB} from "../../redux/actions";
import {URL_IMAGE_PREFIX} from "../../constants";
import './favorite-tab.css';
import Example from './example'
import FavDetail from './fav-detail'
import { Modal, Button } from 'react-bootstrap';
import MovieItem from "../../components/movie-item/movie-item";
import { makeStyles } from '@material-ui/core/styles';


class favMovie extends React.Component {
    state = {
        show: false,
    }


    handleShow = () => {
        this.setState({
            show: true
        })
        console.log(this.props.movieList[0])
    }

    handleClose = () => {
        this.setState({
            show: false
        })
    }




    render() {
        const genreColor = [ {"id": 28, "name": "Action", "color": "Orange"},
            {"id": 12, "name": "Adventure", "color": "lightpink"},
            {"id": 16, "name": "Animation", "color" : "DeepBlueSky"},
            {"id": 35, "name": "Comedy", "color": "MediumOrchid"},
            {"id": 80, "name": "Crime", "color": "violet"},
            {"id": 99, "name": "Documentary", "color": "sandybrown"},
            {"id":18, "name": "Drama", "color": "hotpink"},
            {"id":10751, "name": "Family", "color": "salmon"},
            {"id": 14, "name": "Fantasy", "color": "turquoise"},
            {"id": 36, "name": "History", "color": "khaki"},
            {"id": 27, "name": "Horror", "color": "deeppink"},
            {"id": 10402, "name": "Music", "color": "plum"},
            {"id": 9648, "name": "Mystery", "color": "lavender"},
            {"id": 10749, "name": "Romance", "color": "gold"},
            {"id": 878, "name": "Science Fiction", "color": "skyblue"},
            {"id": 10770, "name": "TV Movie", "color": "yellowgreen"},
            {"id": 53, "name": "Thriller", "color": "brown"},
            {"id": 10752, "name": "War", "color": "red"},
            {"id": 37, "name": "Western", "color": "Goldenrod"}]


        return (
            <div className='fav_container'>
                {
                    this.props.movieList[0].map((movie) => {
                        return (
                            <div className="child">
                                <img className='fav_movie_img' src={URL_IMAGE_PREFIX + movie.poster_path} alt="No url for this movie" title={movie.title} />
                                <div className='button_list'>
                                    <button>Remove</button>
                                    <button>Block</button>
                                    <Button variant="dark" onClick={this.handleShow}>...</Button>
                                    <Example movieDetail = {movie}></Example>
                                </div>
                            </div>
                        );
                    })
                }


            </div>
        )
    }
}

export default connect(
    state => ({movieList: state.movieListReducer}),
    {loadNextPageFromDB}
)(favMovie);
