import React, {useState} from 'react';
import { Modal, Button } from 'react-bootstrap';
import {URL_IMAGE_PREFIX} from "../../constants";
import './example.css'



const Example = (props) => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (

        <>
            <Button variant="primary" onClick={handleShow}>
                ...
            </Button>

            <Modal  className='my_modal' size="lg" show={show} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{props.movieDetail.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body style={{backgroundImage: `url(${URL_IMAGE_PREFIX + props.movieDetail.poster_path})`}}><img src={URL_IMAGE_PREFIX + props.movieDetail.poster_path} alt=""/></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default Example;
