import React from 'react';
import {connect} from "react-redux";
import {loadNextPageFromDB} from "../../redux/actions";
import {URL_IMAGE_PREFIX} from "../../constants";
import './favorite-tab.css';
import MovieItem from "../../components/movie-item/movie-item";
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';


const style = {
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        width: 500,
        height: 450,
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)',
    },
};


class favMovie extends React.Component {



    clickHandler = () => {
        console.log('movieLIst',this.props.movieList)
    }

    render() {

        return (
            <div className={style.root}>
                <GridList cellHeight={180} className={style.gridList}>
                    <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                        <ListSubheader component="div">December</ListSubheader>
                    </GridListTile>
                    {this.props.movieList[0].map((tile) => (
                        <GridListTile key={tile.img}>
                            <img src={URL_IMAGE_PREFIX + tile.poster_path} alt={tile.title} />
                            <GridListTileBar
                                title={tile.title}
                                // subtitle={<span>by: {tile.author}</span>}
                                actionIcon={
                                    <IconButton aria-label={`info about ${tile.title}`} className={style.icon}>
                                        <InfoIcon />
                                    </IconButton>
                                }
                            />
                        </GridListTile>
                    ))}
                </GridList>
                <button onClick={this.clickHandler}>try</button>
                <img src={URL_IMAGE_PREFIX + this.props.movieList[0][0].poster_path} alt="No url for this movie" title={this.props.movieList[0][0].title} />
                {this.props.movieList.length}
            </div>
        )
    }
}

export default connect(
    state => ({movieList: state.movieListReducer}),
    {loadNextPageFromDB}
)(favMovie);
