import React from 'react';
import {connect} from "react-redux";
import {loadNextPageFromDB} from "../../redux/actions";
import {URL_IMAGE_PREFIX} from "../../constants";
import './favorite-tab.css';
import Example from './example'
import { Modal, Button } from 'react-bootstrap';
import MovieItem from "../../components/movie-item/movie-item";
import { makeStyles } from '@material-ui/core/styles';


class favMovie extends React.Component {
    state = {
        show: false,
    }
    handleShow = () => {
        this.setState({
            show: true
        })
    }

    handleClose = () => {
        this.setState({
            show: false
        })
    }




    render() {

        return (
            <div id='bootstrap-overrides' className='fav_container'>
                <Example></Example>

                {
                    this.props.movieList[0].map((movie) => {
                        return (
                            <div className="child">
                                <img className='fav_movie_img' src={URL_IMAGE_PREFIX + movie.poster_path} alt="No url for this movie" title={movie.title} />
                                <div className='button_list'>
                                    <button>Remove</button>
                                    <button>Block</button>
                                    <Button variant="dark" onClick={this.handleShow}>...</Button>
                                    <Modal show={this.state.show} onHide={this.handleClose}>
                                        <Modal.Header closeButton>
                                            <Modal.Title>Modal heading</Modal.Title>
                                        </Modal.Header>
                                        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                                        <Modal.Footer>
                                            <Button variant="secondary" onClick={this.handleClose}>
                                                Close
                                            </Button>
                                            <Button variant="primary" onClick={this.handleClose}>
                                                Save Changes
                                            </Button>
                                        </Modal.Footer>
                                    </Modal>
                                </div>
                            </div>
                        );
                    })
                }


            </div>
        )
    }
}

export default connect(
    state => ({movieList: state.movieListReducer}),
    {loadNextPageFromDB}
)(favMovie);
